mapper.insert:

insert into cm_attendance (name, content, place, 
  creat_time, begin_time, end_time, 
  department_id, user_name, state
  )
values (#{name,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR}, #{place,jdbcType=VARCHAR}, 
  #{creatTime,jdbcType=TIMESTAMP}, #{beginTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, 
  #{departmentId,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{state,jdbcType=VARCHAR}
  )

mapper.select:
select *

	,#{userId} as userId

 from cm_attendance

  
    name = #{name}
  
  
    and content= #{content}
  
  
    and place= #{place}
  
  
    and creat_time= #{creatTime}
  
  
    and begin_time= #{beginTime}
  
  
    and end_time= #{endTime}
  
  
    and department_id= #{departmentId}
  
  
    and user_name= #{userName}
  
  
    and state= #{state}

    SELECT aa.seat,bb.username,bb.avatar FROM cm_attendance_seat aa 
	LEFT JOIN cm_user bb on bb.id = aa.user_id
	WHERE aa.attendance_id = #{id}
